INSERT INTO `wp_comments` VALUES("1", "1", "Một người bình luận WordPress", "wapuu@wordpress.example", "https://wordpress.org/", "", "2022-07-06 10:04:38", "2022-07-06 10:04:38", "Xin chào, đây là một bình luận\nĐể bắt đầu với quản trị bình luận, chỉnh sửa hoặc xóa bình luận, vui lòng truy cập vào khu vực Bình luận trong trang quản trị.\nAvatar của người bình luận sử dụng <a href=\"https://gravatar.com\">Gravatar</a>.", "0", "1", "", "comment", "0", "0");
